pgsql.query:
CREATE SEQUENCE "auto_id_notifications" START 1;

CREATE TABLE "notifications" (
  "id" INTEGER  PRIMARY KEY NOT NULL DEFAULT nextval('auto_id_notifications'),
  "service" varchar(32) NOT NULL,
  "address" varchar(100) NOT NULL,
  "type" varchar(13) NOT NULL
);

sqlite.query:
CREATE TABLE `notifications` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
  `service` varchar(32) NOT NULL,
  `address` varchar(100) NOT NULL,
  `type` varchar(13) NOT NULL
);

mysql.query:
CREATE TABLE `news` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `text` text,
  `new` tinyint(1) unsigned NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

pgsql.query:
CREATE TABLE `news` (
  `id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
  `text` TEXT,
  `new` INTEGER NOT NULL DEFAULT '1'
);

sqlite.query:
CREATE SEQUENCE "auto_id_news" START 1;

CREATE TABLE "news" (
  "id" INTEGER  PRIMARY KEY NOT NULL DEFAULT nextval('auto_id_news'),
  "text" TEXT NOT NULL,
  "new" INTEGER NOT NULL DEFAULT '1'
);
